import * as React from "react";
import classNames from "classnames";

export type InputProps = React.InputHTMLAttributes<HTMLInputElement>;

const Input = React.forwardRef<HTMLInputElement, InputProps>(
  ({className, type = "text", ...props}, ref) =>{
    return (
      <input
        type={type}
        className={classNames(
          "flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed",
          className
        )}
        ref={ref}
{...props}
      />
    );
}
);

Input.displayName = "Input";

export default Input;

import Input from './Input'; // Adjust the path as necessary

const App = () =>{
  return (
    <div>
      <Input placeholder="Enter text here" />
    </div>
  );
};
