// source.ts
export interface Source{
    id: string;
    title: string;
    url: string;
}

// Import the Source interface
import{Source}from './source';

// Create a source object
const mySource: Source ={
    id: '1',
    title: 'Understanding TypeScript',
    url: 'https://example.com/understanding-typescript'
};

console.log(mySource);

// Function to display source information
function displaySource(source: Source): void{
    console.log(`Title: ${source.title}`);
    console.log(`URL: ${source.url}`);
}

displaySource(mySource);

// Array of Source objects
const sources: Source[] =[
{id: '1', title: 'Understanding TypeScript', url: 'https://example.com/understanding-typescript'},
{id: '2', title: 'Learning JavaScript', url: 'https://example.com/learning-javascript'}
];

console.log(sources);
